/* Here we override the standard configuration of the wz_tooltip.js. This file must be included just below the line where the wz_tooltip.js is
   included. The advantage of this procedure is that wz_tooltip.js is untouched and can be re-used by other web pages */
config. BorderWidth   = 1;
config. TitleFontFace = 'Verdana,sans-serif';
config. TitleBgColor  = '#565656';
config. BorderColor   = '#565656';
config. BgColor       = '#E8E8E8';
config. FontColor     = '#464646';
config. FontSize      = '9pt';
config. Width         = -500;
config. Delay         = 800;
config. FadeIn        = 200;
config. FadeOut       = 200;
config. ClickClose    = true;
config. FollowMouse   = false;
config. OffsetX       = 40;
config. OffsetY       = 0;
config. JumpHorz      = true;

